import React, {Component} from 'react';
import Post from './components/post';
import PostForm from './components/postForm';

const postsArray = [
  {
    content: 'Texto do content',
    time: '15:20',
    author: 'Heleno',
    initialLikes: 2
  },
  {
    content: 'Texto dois do content',
    time: '15:30',
    author: 'Heleno',
    initialLikes: 3
  }
]

class App extends Component {
  constructor(){
    super();
    this.state = {
      posts: postsArray
    }
  }

  insertPost(post){
    const myPosts = this.state.posts;
    myPosts.push(post);
    this.setState({posts: myPosts});
  }

  render(){
    return(
        <div>
          <h1> Minha rede social </h1>
          <PostForm onCreate={this.insertPost.bind(this)}/>
          {
            this.state.posts.map((post,i) => {
              return  (
                      <Post key = {i} post={post} />
              )
            })
          }
        </div>
    )
  }
}

export default App;
