class UserDetails extends Component {

  constructor(){
    super();
    this.state = {
      user: null
    }
  }

  componentDidMount(){
    console.log(this.props.match.params.id);
    const users = JSON.parse(localStorage.getItem('users'));
    const user = posts.filter(savedUser => {
      return savedUser.id === this.props.match.params.id;
    }).pop();
    this.setState({user});
  }

  render(){
    if(this.state.post===null){
      return (
        <div>Loading</div>
      )
    }else{
      return(
          <div>
          {this.state.user.name}
          </div>
      )
    }

  }
}

export default UserDetails;
