import React, {Component} from 'react';

class PostForm extends Component{
  constructor(){
    super();
    this.state = {
      text: ''
    }
  }



  render(){
    return(
      <div>
        <h3>Novo post</h3>
        <input
        onChange={(event) => {
          console.log(event);
        }}
        value={this.state.text}
        placeholder="Digite o texto da sua postagem"
        style={{
          width: '80%',
          padding: '10px',
          margin: '10px'

        }}/>
        <button>Postar</button>
      </div>
    )
  }
}

export default PostForm;
