import React, {Component} from 'react';
import "../post.css";


class Post extends Component{
  render(){
    return(
      <div className={"post"}>
        <h3>{this.props.text}</h3>
        <small>{this.props.time}}</small>
        <div style={likeLine}>
          <p> Likes: {this.props.likes} </p>
          <button
            style={{
              'backgroundColor':'blue',
              'color':'white',
              'fontSize': 20,
              'fontWeight': 'bold',
              'border': 'none',
              'borderRadius': 10,
              'padding': 5
            }}
          >Like</button>
        </div>
      </div>
    )
  }
}

const likeLine={
  'display': 'flex',
  'justifyContent': 'space-around'
}

export default Post;
