class User extends Component{
  constructor(props){
    super(props);
    this.user = props.user;
  }

  render(){
    return(
      <div style={
        {
          border: '5px solid'

        }
      }>
      <div style={
        {
          margin: 'auto',
          width: '50%',
          border: '5px solid blue'
        }
      }>
        <img alt='' src={this.user.profilePic} width='60px' height='50px'/>
        <br/>
        <span style={{'text-align': 'center'}}>{this.user.name}</span>
      </div>
      </div>
    )
  }
}
export default User;
