import React, {Component} from 'react';

class PostForm extends Component{
  constructor(){
    super();
    this.state = {
      text: ''
    }
  }

  createPost(){
    const newPost = {
      content: this.state.text,
      author: 'Heleno',
      time: new Date().getTime(),
      initialLikes: 0
    }
    console.log(newPost);
    this.props.onCreate(newPost);
  }

  render(){
    console.log(this.props.onCreate)
    return(
      <div>
        <h3>Novo post</h3>
        <input
        onChange={(event) => {
          const value = event.target.value;
          this.setState({text: value})
        }}
        value={this.state.text}
        placeholder="Digite o texto da sua postagem"
        style={{
          width: '80%',
          padding: '10px',
          margin: '10px'

        }}/>
        <button
        onClick={()=>this.createPost()}
        style={
          {
            'width': '12%',
            'backgroundColor':'blue',
            'color':'white',
            'fontSize': 20,
            'fontWeight': 'bold',
            'border': 'none',
            'borderRadius': 10,
            'padding': 5
          }
        }>Postar</button>
      </div>
    )
  }
}

export default PostForm;
