import React, {Component} from 'react';
import Post from './components/post';
import PostForm from './components/postForm';

const postsArray = []

class App extends Component {
  constructor(){
    super();
    this.state = {
      posts: postsArray
    }
  }

  insertPost(post){
    const myPosts = this.state.posts;
    myPosts.unshift(post);
    this.setState({posts: myPosts});
    this.saveInStorage();
  }

  saveInStorage(){
    const posts = JSON.stringify(this.state.posts);
    console.log(posts);
    localStorage.setItem('savedPosts', posts);
  }

  render(){
    return(
        <div>
          <h1> Minha rede social </h1>
          <PostForm onCreate={this.insertPost.bind(this)}/>
          {
            this.state.posts.map((post,i) => {
              return  (
                      <Post key = {post.time} post={post} />
              )
            })
          }
        </div>
    )
  }
}

export default App;
